import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import logo from './FRNPhoto.jpg';

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [isVisible, setIsVisible] = useState(true);
  const [prevScrollPos, setPrevScrollPos] = useState(window.pageYOffset);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.pageYOffset;
      const isVisible = prevScrollPos > currentScrollPos;

      setIsVisible(isVisible);
      setPrevScrollPos(currentScrollPos);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollPos]);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className={isVisible ? 'navbar' : 'navbar hidden'} style={{ backgroundColor: 'white' }}>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            <img src={logo} alt='Logo' className='navbar-logo-img' style={{ width: '140px', height: 'auto', marginRight: '120px' }} />
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/about' className='nav-links' onClick={closeMobileMenu}>
                About
              </Link>
              <ul className='dropdown-menu'>
                <li>
                  <Link to='/about/team' className='dropdown-link' onClick={closeMobileMenu}>
                    Who We Are
                  </Link>
                </li>
                <li>
                  <Link to='/Purpose' className='dropdown-link' onClick={closeMobileMenu}>
                    What We Do
                  </Link>
                </li>
                {/* Add more dropdown items as needed */}
              </ul>
            </li>
            <li className='nav-item'>
              <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
                Services
              </Link>
            </li>
            <li className='nav-item'>
              <a href="https://docs.google.com/forms/d/e/1FAIpQLScBrPF6CzX7IMW7iMPVf5_I4_p8HPEfc0yHI1wGVlNYLw6F_A/viewform" className='nav-links' target="_blank" rel="noopener noreferrer" onClick={closeMobileMenu}>
                Donate
              </a>
            </li>
            <li className='nav-item'>
              <Link to='/contact' className='nav-links' onClick={closeMobileMenu}>
                Contact Us
              </Link>
            </li>
            <li className='nav-item'>
              <a href="https://www.foodrecoverynetwork.org/" className='nav-links' target="_blank" rel="noopener noreferrer" onClick={closeMobileMenu}>
                FRN National Website
              </a>
            </li>
          </ul>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
