import React from 'react';
import photo from './default.jpg';

function About() {
  const people = [
    { name: "John Doe", introduction: "Hi, I'm John! I love coding and exploring new technologies.", image: logo },
    { name: "Jane Smith", introduction: "Hey there! I'm Jane, passionate about design and user experience.", image: logo },
    { name: "Michael Johnson", introduction: "Hello, I'm Michael. I enjoy solving complex problems with elegant solutions.", image: logo },
    { name: "Emily Brown", introduction: "Hi everyone! I'm Emily. I'm a nature enthusiast and an avid reader.", image: logo },
    { name: "David Lee", introduction: "Greetings! I'm David, a music lover and amateur chef.", image: logo },
    // Add more people as needed
  ];

  return (
    <div>
      <h1 style={{ textAlign: "center" }}>About Us</h1>
      <div style={{ display: "flex", justifyContent: "center" }}>
        <div style={{ maxWidth: "800px" }}>
          <p style={{ textAlign: "center" }}>Meet our awesome team:</p>
          <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
            {people.map(person => (
              <div key={person.name} style={{ margin: "10px", textAlign: "center" }}>
                <img src={require(`./images/${person.image}`).default} alt={person.name} style={{ width: "200px", height: "200px", borderRadius: "50%" }} />
                <h3>{person.name}</h3>
                <p>{person.introduction}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default About;
