import React from 'react';
import photo from './Images/default.jpg';
import team from './Images/Team.png';

function About() {
  const people = [
    { name: "John Doe", introduction: "Hi, I'm John! I love coding and exploring new technologies.", image: photo },
    { name: "Jane Smith", introduction: "Hey there! I'm Jane, passionate about design and user experience.", image: photo },
    { name: "Michael Johnson", introduction: "Hello, I'm Michael. I enjoy solving complex problems with elegant solutions.", image: photo },
    { name: "Emily Brown", introduction: "Hi everyone! I'm Emily. I'm a nature enthusiast and an avid reader.", image: photo },
    { name: "David Lee", introduction: "Greetings! I'm David, a music lover and amateur chef.", image: photo },
    // Add more people as needed
  ];

  return (
    <div style={{ backgroundColor: "#f0f0f0", position: "relative" }}>
      <div style={{ overflow: "hidden" }}>
        <img src={team} alt="Team Photo" style={{ width: "100%", height: "auto", objectFit: "cover", objectPosition: "50% 25%", filter: "brightness(0.8)" }} /> {/* Team photo at the top */}
        <h1 style={{ position: "absolute", top: "12%", left: "50%", transform: "translate(-50%, -50%)", color: "white", fontSize: "60px" }}>OUR TEAM</h1>
      </div>
      <div style={{ position: "absolute", left: "20px", top: "50%", transform: "translateY(-50%)" }}>
        {people.map(person => (
          <img key={person.name} src={person.image} alt={person.name} style={{ width: "100px", height: "100px", borderRadius: "50%", marginBottom: "10px" }} />
        ))}
      </div>
    </div>
  );
}

export default About;
